mapper.select:
select a.id,owntbl.name as owner, a.name,a.start_date,a.end_date,a.cost,a.description, a.create_time,cretbl.name as create_by,a.edit_time,edittbl.name as edit_by from tbl_activity a join tbl_user owntbl on a.owner = owntbl.id join tbl_user cretbl on a.create_by = cretbl.id left join tbl_user edittbl on a.edit_by = edittbl.id
and a.name like concat('%',#{name},'%')
and owntbl.name like concat('%',#{owner},'%')
and a.start_date >= #{startDate}
and a.end_date <= #{endDate}
order by a.create_time desc

mapper.delete:
delete from tbl_activity
where id in

    #{id}
